import './App.css';
import Header from './components/Header';
import { useState, useEffect } from 'react';
import InfiniteScroll from 'react-infinite-scroll-component';
import LoaderComponent from './components/Loader';
import EndComponent from './components/End';
import Conetent from './components/Conetent';


function App() {
  const [searchTerm, setSearchTerm] = useState("");
  const [viewMode, setViewMode] = useState(0);
  const [items, setItems] = useState([]);
  const [hasMore, sethasMore] = useState(true);
  const [page, setpage] = useState(20);
  const itemsPerPage = 60;

  const getContent = async () => {
    const res = await fetch(
      'http://xoosha.com/ws/1/test.php?offset=' + itemsPerPage * page
    );
    const data = await res.json();
    setItems([...items, ...data]);
    setpage(page + 1);
  }
  useEffect(() => {
    getContent();
  }, []);

  return (
    <div className="App">
      <Header viewMode={viewMode} setViewMode={setViewMode} />
      <div
        id="scrollableDiv"
        style={{
          height: '100vh',
          overflow: 'auto',
          display: 'flex',
          flexDirection: 'column',
        }}
      >
        <InfiniteScroll
          dataLength={items.length}
          next={getContent}
          style={{ display: 'flex', flexDirection: 'column' }}
          scrollableTarget="scrollableDiv"
          hasMore={hasMore}
          loader={<LoaderComponent />}
          endMessage={<EndComponent />}
        >
          <div >
            {items.map((item) => {
              console.log(item.image_url);
              return <Conetent key={item.page_id} image_url={item.image_url}></Conetent>
            })}
          </div>
        </InfiniteScroll>
      </div>

    </div>
  );
}

export default App;
