import './App.css';
import Header from './components/Header';
import { useState, useEffect } from 'react';
import InfiniteScroll from 'react-infinite-scroll-component';
import LoaderComponent from './components/Loader';
import EndComponent from './components/End';
import Conetent from './components/Content';


function App() {
  const [searchTerm, setSearchTerm] = useState("");
  const [viewMode, setViewMode] = useState(0);
  const [items, setItems] = useState([]);
  const [hasMore, sethasMore] = useState(true);
  const [page, setpage] = useState(0);
  const itemsPerPage = 60;

  const getContent = async () => {
    const res = await fetch(
      'http://xoosha.com/ws/1/test.php?offset=' + itemsPerPage * page
    );
    const data = await res.json();
    setItems([...items, ...data]);
    setpage(page + 1);
  }
  useEffect(() => {
    getContent();
  }, []);

  return (
    <div className="App">
      <Header viewMode={viewMode} setViewMode={setViewMode} />
      <div id="scrollableDiv">
        <InfiniteScroll
          dataLength={items.length}
          next={getContent}
          style={{ display: 'inline-grid', grid: "grid: masonry / 50px 100px auto", flexWrap: 'wrap', width: '100%', overflow: "hidden" }}
          scrollableTarget="scrollableDiv"
          hasMore={hasMore}
          loader={<LoaderComponent />}
          endMessage={<EndComponent />}
        >
          {items.map((item) => {
            console.log(item);
            return <Conetent key={item.page_id} image_url={item.image_url} name={item.name}></Conetent>
          })}
        </InfiniteScroll>
      </div>

    </div>
  );
}

export default App;
