import './App.css';
import Header from './components/Header';
import { useState, useEffect } from 'react';
import InfiniteScroll from 'react-infinite-scroll-component';
import LoaderComponent from './components/Loader';
import EndComponent from './components/End';
import Conetent from './components/Content';


function App() {
  const [searchTerm, setSearchTerm] = useState("");
  const [viewMode, setViewMode] = useState(0);
  const [items, setItems] = useState([]);
  const [hasMore, sethasMore] = useState(true);
  const [page, setpage] = useState(0);
  const itemsPerPage = 60;

  const getContent = async () => {
    const res = await fetch(
      'http://xoosha.com/ws/1/test.php?offset=' + itemsPerPage * page
    );
    const data = await res.json();
    setItems([...items, ...data]);
    setpage(page + 1);
  }
  useEffect(() => {
    if (searchTerm !== "")
      setItems([...items.filter(item => item.description.toLowerCase().includes(searchTerm.toLowerCase()))]);
  }, [searchTerm]);

  getContent();

  return (
    <div className="App">
      <Header viewMode={viewMode} setViewMode={setViewMode} setSearchTerm={setSearchTerm} />
      <div id="scrollableDiv">
        <InfiniteScroll
          dataLength={items.length}
          next={getContent}
          scrollableTarget="scrollableDiv"
          hasMore={hasMore}
          loader={<LoaderComponent />}
          endMessage={<EndComponent />}
          className="masnory-container"
        >
          {viewMode === 0 &&
            items.map((item) => {
              return <Conetent viewMode={viewMode} key={item.page_id} image_url={item.image_url} name={item.name}></Conetent>
            })
          }
          {viewMode === 1 &&
            items.map((item) => {
              return <Conetent viewMode={viewMode} key={item.page_id} image_url={item.image_url} name={item.name}></Conetent>
            })
          }
        </InfiniteScroll>
      </div>

    </div >
  );
}

export default App;
