import './App.css';
import Header from './components/Header';
import { useState, useEffect } from 'react';
import InfiniteScroll from 'react-infinite-scroll-component';
import LoaderComponent from './components/Loader';
import EndComponent from './components/End';
import Conetent from './components/Content';
import ReactBricks from "react-bricks-infinite";


function App() {
  const [searchTerm, setSearchTerm] = useState("");
  const [viewMode, setViewMode] = useState(0);
  const [items, setItems] = useState([]);
  const [hasMore, sethasMore] = useState(true);
  const [page, setpage] = useState(0);
  const itemsPerPage = 60;

  const getContent = async () => {
    const res = await fetch(
      'http://xoosha.com/ws/1/test.php?offset=' + itemsPerPage * page
    );
    const data = await res.json();
    setItems([...items, ...data]);
    setpage(page + 1);
  }
  useEffect(() => {
    getContent();
  }, []);

  return (
    <div className="App">
      <Header viewMode={viewMode} setViewMode={setViewMode} />
      <ReactBricks
        containerId={this.containerId}
        loadMoreBricks={this.loadMore}
        hasMoreBricks={this.state.hasMoreBricks}
        reRender={this.state.reRender}
        bricks={this.state.bricks}
        defaultLoaderStyle={this.defaultLoaderStyle}
      />

    </div >
  );
}

export default App;
